// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "./CryptoPotsBase.sol";

/// @notice This contract is controlling the creation of pots.
contract CryptoPotsController is CryptoPotsBase {
    struct Settings {
        // Duration: how much blocks will a pot game exist
        uint32 duration;
        // The amount of total fee from pot win
        // 5 means 1/5 part of the winning stake 
        // (Excluding the amount of the winners bet)
        uint32 devFee;
        // The part that will go to the creator.
        // 4 means 1/4 of the total fee wil go to developer.
        uint32 creatorFee;
    }

    struct Pot {
        Settings settings;
        // A block from where the last game started
        uint256 blockStart;
        // Is the game started?
        bool started;
        uint256 balance;

        // Creator address to pay creator fee
        address payable creator;

        // Current bets in volume
        mapping(address => uint256) bets;
        // bets keys, list af all current participants.
        address[] participants;
    }

    // Each pot will have a uint256 id
    mapping(uint256 => Pot) pots;
    // Storing available pots for playing, a.k.a. dict keys
    uint256[] activePotsList;
    // Last id of created pot
    uint256 lastPotID;
    uint32 constant CREATOR_FEE = 5;

    // Lists of creators PotIDs
    mapping(address => uint256[]) creatorPots;

    event newPotCreated(uint256 id, address payable creator);

    // Everyone can create a pot.
    // Will return an id for a pot and a new smart contract address
    function createPot(uint32 duration, uint32 fee) public returns (uint256) {
        lastPotID++;
        uint256 newPotID = lastPotID;

        activePotsList.push(newPotID);

        pots[newPotID].creator = msg.sender;
        pots[newPotID].settings = Settings(duration, fee, CREATOR_FEE);
        reset(newPotID);

        creatorPots[msg.sender].push(newPotID);

        emit newPotCreated(newPotID, msg.sender);
        return (newPotID);
    }

    // Get pot ids, created by creator
    function getCreatorPots(address creator)
        public
        view
        returns (uint256[] memory)
    {
        return creatorPots[creator];
    }

    // Get personal created pot ids
    function getPersonalPots() public view returns (uint256[] memory) {
        return getCreatorPots(msg.sender);
    }

    // Get the list of active pots
    // The same as getting activePotsList
    function getActivePotsIDs() public view returns (uint256[] memory) {
        return activePotsList;
    }

    constructor() CryptoPotsBase() {
        lastPotID = 0;
    }

    // This modifier will translate the actions on the sub contract
    modifier potWrapper(uint256 potID) {
        require(potID <= lastPotID, "potID does not exist");
        _;
    }

    // Get the amount of weis in a pot
    function getPotVolume(uint256 potID) 
        public 
        view 
        potWrapper(potID) 
        returns (uint256) 
    {
        return pots[potID].balance;
    }

    event PayoutLog(
        uint256 potID,
        address who,
        uint256 amount,
        string data
    );
    
    // This function will decide a winner when the pot is finished
    // TODO: add some rewards for spending money on calculating winners
    function calculateWinner(uint256 potID) 
        public  
        potWrapper(potID)
    {
        Pot storage pot = pots[potID];
        require(pot.started && isPotFinished(potID));

        address payable winner = getWinner(potID);
        uint256 fee = (pot.balance - pot.bets[winner]) / pot.settings.devFee;
        uint256 creatorFee = fee / pot.settings.creatorFee;
        uint256 devFee = fee - creatorFee;
        uint256 winAmount = pot.balance - fee;

        emit PayoutLog(potID, developer, devFee, "dev");
        emit PayoutLog(potID, pot.creator, creatorFee, "creator");
        emit PayoutLog(potID, winner, winAmount, "winner");

        developer.transfer(devFee);
        pot.creator.transfer(creatorFee);
        winner.transfer(winAmount);

        reset(potID);
    }

    event BetLog(
        uint256 potID, 
        address who, 
        uint256 amount,
        uint256 aggregatedAmount,
        uint256 potVolume
    );

    // Make a bet in a pot
    // If the game has not started, will start the game
    // If the game has finished, will automatically calculate the winner of prev one
    // and then place a bet in a new one
    function makeBet(uint256 potID)
        public 
        payable 
        potWrapper(potID)
    {
        require(!isPotFinished(potID), "Pot is already finished");
        Pot storage pot = pots[potID];
        address player = msg.sender;
        uint256 amount = msg.value;
        bool isParticipant = false;
        for (uint32 i = 0; i < pot.participants.length; i++) {
            if (pot.participants[i] == player) {
                isParticipant = true;
                break;
            }
        }
        if (!isParticipant) pot.participants.push(player);

        pot.bets[player] += amount;
        pot.balance += amount;

        if (!pot.started) startPot(potID);
        emit BetLog(
            potID, 
            msg.sender, 
            msg.value, 
            pot.bets[player], 
            pot.balance
        );
    }

    // bool: is started or not
    function isPotStarted(uint256 potID) 
        public 
        view 
        potWrapper(potID) 
        returns (bool) 
    {
        return pots[potID].started;
    }

    // The amount of blocks remaining for this game
    function remainingBlocks(uint256 potID) 
        public 
        view 
        potWrapper(potID) 
        returns (int256) 
    {
        return int256(pots[potID].blockStart + pots[potID].settings.duration) - int256(block.number);
    }

    // bool: is finished or not
    function isPotFinished(uint256 potID)
        public 
        view 
        potWrapper(potID) 
        returns (bool) 
    {
        return remainingBlocks(potID) <= 0 && pots[potID].started;
    }

    // Get participants list
    function getParticipants(uint256 potID) 
        public 
        view 
        potWrapper(potID) 
        returns (address[] memory) 
    {
        return pots[potID].participants;
    }

    // Get a bet of particular player
    function getParticipantBet(uint256 potID, address participant)
        public
        view
        potWrapper(potID)
        returns (uint256)
    {
        return pots[potID].bets[participant];
    }

    function getPotDuration(uint256 potID) public view returns (uint32){
        return pots[potID].settings.duration;
    }

    function getPotOwner(uint256 potID) public view returns (address){
        return pots[potID].creator;
    }

    function reset(uint256 potID) private potWrapper(potID){
        Pot storage pot = pots[potID];
        pot.balance = 0;
        pot.started = false;

        for (uint32 i = 0; i < pot.participants.length; i++) {
            pot.bets[pot.participants[i]] = 0;
        }
        delete pot.participants;
    }

    function startPot(uint256 potID) private potWrapper(potID){
        Pot storage pot = pots[potID];
        pot.started = true;
        pot.blockStart = block.number;
    }

        // Returns a pseudo-random winner.
    function getWinner(uint256 potID) private view returns (address payable) {
        Pot storage pot = pots[potID];
        uint256 winningNumber = randInt(getPotVolume(potID));
        uint256 currentSum = 0;

        for (uint256 i = 0; i < pot.participants.length; i++) {
            address payable participant = payable(pot.participants[i]);
            currentSum += pot.bets[participant];
            if (currentSum >= winningNumber) {
                return participant;
            }
        }

        // The code should not be here
    }

    /// @return a random uint256 from 0 to last
    // TODO: change a random function to make it less vulnerable
    function randInt(uint256 last) private view returns (uint256) {
        uint256 seed = uint256(
            keccak256(
                abi.encodePacked(
                    block.timestamp +
                        block.difficulty +
                        ((
                            uint256(keccak256(abi.encodePacked(block.coinbase)))
                        ) / (block.timestamp)) +
                        block.gaslimit +
                        ((uint256(keccak256(abi.encodePacked(msg.sender)))) /
                            (block.timestamp)) +
                        block.number
                )
            )
        );

        return seed - ((seed / last) * last);
    }
}
